import axios from "axios";
export default {
  name: "NoteAdd",
  data: function data() {
    return {
      title: null,
      body: null,
      message: null,
      status: ''
    };
  },
  methods: {
    postNote: function postNote() {
      var _this = this;

      axios.post("http://localhost:3000/notes", {
        title: this.title,
        body: this.body
      }).then(function (response) {
        return _this.message = response.data;
      }).catch(function (error) {
        _this.status = 'Title cannot be empty!';
      });
      this.checkTitle();
    },
    checkTitle: function checkTitle() {
      if (this.title == null) {
        this.status = "Title cannot be empty!";
      } else {
        this.title = "";
        this.body = "";
        this.status = "Note saved!";
      }
    }
  },
  watch: function watch() {
    this.checkTitle();
  }
};
